package commonchild

import "testing"

func Test_commonChild(t *testing.T) {
	type args struct {
		a string
		b string
	}
	tests := []struct {
		name string
		args args
		want int
	}{
		{"Same string single character", args{"a", "a"}, 1},
		{"two character consecutive substring", args{"abc", "bcd"}, 2},
		{"Sample 0", args{"harry", "sally"}, 2},
		{"Sample 1", args{"aa", "bb"}, 0},
		{"Sample 2", args{"shinchan", "noharaaa"}, 3},
		{"Sample 3", args{"abcdef", "fbdamn"}, 2},
		{"Test Case 1", args{"WEWOUCUIDGCGTRMEZEPXZFEJWISRSBBSYXAYDFEJJDLEBVHHKS",
			"FDAGCXGKCTKWNECHMRXZWMLRYUCOCZHJRRJBOAJOQJZZVUYXIC"}, 15},
		{"Test Case 2", args{"ELGGYJWKTDHLXJRBJLRYEJWVSUFZKYHOIKBGTVUTTOCGMLEXWDSXEBKRZTQUVCJNGKKRMUUBACVOEQKBFFYBUQEMYNENKYYGUZSP",
			"FRVIFOVJYQLVZMFBNRUTIYFBMFFFRZVBYINXLDDSVMPWSQGJZYTKMZIPEGMVOUQBKYEWEYVOLSHCMHPAZYTENRNONTJWDANAMFRX"}, 27},
		{"Test Case 3", args{"",
			""}, 155},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := commonChild(tt.args.a, tt.args.b); got != tt.want {
				t.Errorf("common child() = %v, want %v", got, tt.want)
			}
		})
	}
}
